# turtle图形显示占卜结果
def show_results(gua_b, gua_c):
    global g1, g2
    g1 = GUA(-100)
    g2 = GUA(100)

    ht(); penup(); goto(0, 200)
    write("周易占卜", align="center", font=("隶书", 60, ("bold")))

    penup(); goto(0, 200)
    for ch in gua_b:
        g1.push(YAO(ch))
    if gua_b != gua_c:
        for ch in gua_c:
            g2.push(YAO(ch))

    result_guas, result_text = IChing.get_results(gua_b, gua_c)

    penup(); goto(0, -100)
    write(result_guas, align="center", font=("隶书", 40, ("bold")))
    penup(); goto(0, -130); color('red')
    write(result_text, align="center", font=("隶书", 20, ("bold")))

    penup(); goto(0, -200)
    yin(50, "black", "white")
    yin(50, "white", "black")
    return

# 阳爻阴爻图形
class YAO(Turtle):
    def __init__(self, ch):
        Turtle.__init__(self, shape="square", visible=False)
        self.pu()
        self.shapesize(1.2, 6 * 1.5, 2)
        if ch == '1':
            # 阳爻白色
            self.fillcolor(1, 1, 1)
        else:
            # 阴爻黑色
            self.fillcolor(0, 0, 0)
        self.st()


# 卦图形，每卦包含六爻
class GUA(list):
    def __init__(self, x):
        self.x = x

    def push(self, yao):
        yao.setx(self.x)
        yao.sety(-20 + 34 * len(self))
        self.append(yao)


def play():
    onkey(None, "space")
    clearscreen()
    main()


# 阴阳鱼小图
def yin(radius, color1, color2):
    speed('fastest')
    down()
    color("black", color1)
    begin_fill()
    circle(radius / 2., 180)
    circle(radius, 180)
    left(180)
    circle(-radius / 2., 180)
    end_fill()
    left(90)
    up()
    forward(radius * 0.35)
    right(90)
    down()
    color(color1, color2)
    begin_fill()
    circle(radius * 0.15)
    end_fill()
    left(90)
    up()
    backward(radius * 0.35)
    down()
    left(90)


# 测试算得各卦的概率
def test(n):
    # 算n次频率统计
    R = []
    t = time.perf_counter()
    for _ in range(n):
        gua_b, gua_c = IChing.get_guas()
        R.append(IChing.get_gua_name(gua_b))
    print('-------TEST-------')
    print('After %d times & %.8f seconds, the result is:' % (n, time.perf_counter() - t))
    C = Counter(R)
    print(C)
    L = list(C.values())
    print('From the result, mean=%.2f, std=%.2f, std/mean=%.2f' % (
    np.mean(L), np.std(L, ddof=1), np.std(L, ddof=1) / np.mean(L)))
    print('-----TEST END-----')


